
public class MainClass4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		//덧셈
		System.out.println(a + " + " + b + " = " + (a+b));
		//뺄셈
		System.out.println(a + " - " + b + " = " + (a-b));
		//곱셈
		System.out.println(a + " * " + b + " = " + (a*b));
		//나눗셈
		System.out.println(a + " / " + b + " = " + (a/b));
		//나머지
		System.out.println(a + " % " + b + " = " + (a%b));
	}

}
